import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http';
import { environment} from '../../environments/environment';
import {Observable} from 'rxjs';
import { Notes } from '../Interfaces/notes';

@Injectable({
  providedIn: 'root'
})
export class NotesService {

  private endpoint: string =  environment.endPoint
  private apiUrl:string = this.endpoint + 'notes/'

  constructor(private http:HttpClient) { }

  getList():Observable<Notes[]>{
    return this.http.get<Notes[]>(`${this.apiUrl}list`);
  }
  add(model:Notes):Observable<Notes>{
    return this.http.post<Notes>(`${this.apiUrl}save`,model);
  }

  update(IdNote:number, model:Notes):Observable<Notes>{
    return this.http.put<Notes>(`${this.apiUrl}update/${IdNote}`,model);
  }

  delete(IdNote:number):Observable<void>{
     return this.http.delete<void>(`${this.apiUrl}delete/${IdNote}`);
  }

}
